chapter.para:
The webadmin servlet running inside the &serv-httpd;
service has a special page for querying, displaying and tracking
these alarms.

section.programlisting:
#!/bin/sh case $# in 0) PORT=60001 ;; 1) PORT=${1} ;; *) echo "Usage: $(basename $0) [PORT]" >&2 exit 1 ;; esac DC=/etc/dcache CL=/usr/share/dcache/classes
CP=. CP=${CP}:`find ${CL} -name "activation-*.jar"` CP=${CP}:`find ${CL} -name "datanucleus-api-jdo-*.jar"` CP=${CP}:`find ${CL} -name "datanucleus-cache*.jar"` CP=${CP}:`find ${CL} -name "datanucleus-core*.jar"` CP=${CP}:`find ${CL} -name "datanucleus-xml*.jar"` CP=${CP}:`find ${CL} -name "dcache-core*.jar"` CP=${CP}:`find ${CL} -name "guava-*.jar"` CP=${CP}:`find ${CL} -name "jargs-*.jar"` CP=${CP}:`find ${CL} -name "jaxb-*.jar" | tr '\n' ':'` CP=${CP}:`find ${CL} -name "jaxrpc-*.jar" | tr '\n' ':'` CP=${CP}:`find ${CL} -name "jdo-api-*.jar"` CP=${CP}:`find ${CL} -name "json-*.jar"` CP=${CP}:`find ${CL} -name "log4j-over-slf4j-*.jar"` CP=${CP}:`find ${CL} -name "logback-classic-*.jar"` CP=${CP}:`find ${CL} -name "logback-core-*.jar"` CP=${CP}:`find ${CL} -name "mail-*.jar"` CP=${CP}:`find ${CL} -name "slf4j-api-*.jar"`
java -cp ${CP} ch.qos.logback.classic.net.SimpleSocketServer ${PORT} /var/lib/dcache/alarms/logback-server.xml &
